DROP TABLE IF EXISTS `wow_faction_npcs`;
CREATE TABLE `wow_faction_npcs` (
  `faction_id` int(11) NOT NULL,
  `npc_id` int(11) NOT NULL,
  `npc_key` varchar(255) NOT NULL,
  `npc_name` text NOT NULL,
  PRIMARY KEY (`faction_id`,`npc_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `wow_faction_template`;
CREATE TABLE `wow_faction_template` (
  `factiontemplateID` smallint(4) unsigned NOT NULL,
  `factionID` smallint(4) NOT NULL,
  `A` smallint(1) NOT NULL COMMENT 'Aliance: -1 - hostile, 1 - friendly, 0 - neutral',
  `H` smallint(1) NOT NULL COMMENT 'Horde: -1 - hostile, 1 - friendly, 0 - neutral',
  PRIMARY KEY (`factiontemplateID`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=FIXED;


DROP TABLE IF EXISTS `wow_factions`;
CREATE TABLE `wow_factions` (
  `key` varchar(255) NOT NULL,
  `id` int(11) NOT NULL,
  `name` text NOT NULL,
  `intro_es` text NOT NULL,
  `desc_es` text NOT NULL,
  `faction` int(11) NOT NULL,
  `leader_es` text NOT NULL,
  `intendant_es` text NOT NULL,
  `location_es` text NOT NULL,
  `leader_id` int(11) NOT NULL,
  `intendant_id` int(11) NOT NULL,
  `expansion` int(11) NOT NULL,
  PRIMARY KEY (`key`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `wow_profession_bonuses`;
CREATE TABLE `wow_profession_bonuses` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `profession_id` int(11) NOT NULL,
  `icon` varchar(255) NOT NULL,
  `name_es` text NOT NULL,
  `desc_es` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=70 DEFAULT CHARSET=utf8 AUTO_INCREMENT=70 ;


DROP TABLE IF EXISTS `wow_profession_data`;
CREATE TABLE `wow_profession_data` (
  `key` varchar(255) NOT NULL,
  `id` int(11) NOT NULL,
  `name_es` text NOT NULL,
  `intro_es` text NOT NULL,
  `desc_es` text NOT NULL,
  `howto_es` text NOT NULL,
  `icon` varchar(255) NOT NULL,
  PRIMARY KEY (`key`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `wow_profession_related`;
CREATE TABLE `wow_profession_related` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `profession_id` int(11) NOT NULL,
  `prof_key` varchar(255) NOT NULL,
  `icon` varchar(255) NOT NULL,
  `prof_name_es` text NOT NULL,
  `prof_desc_es` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 AUTO_INCREMENT=18 ;

